import React, { useState, useEffect } from "react";
import axios from "axios";

const UnpaidCosts = () => {
  const [costs, setCosts] = useState([]);
  const [newCost, setNewCost] = useState({
    description: "",
    amount: "",
    urgency: "",
  });

  useEffect(() => {
    fetchCosts();
  }, []);

  const fetchCosts = async () => {
    const result = await axios.get("http://localhost:5000/unpaid_costs");
    setCosts(result.data);
  };

  const handleAddCost = async () => {
    await axios.post("http://localhost:5000/unpaid_costs", {
      date: new Date().toISOString().slice(0, 10),
      description: newCost.description,
      amount: parseFloat(newCost.amount),
      urgency: parseInt(newCost.urgency, 10),
    });
    fetchCosts();
    setNewCost({ description: "", amount: "", urgency: "" });
  };

  return (
    <div>
      <h2>Unpaid Costs</h2>
      <input
        type="text"
        placeholder="Description"
        value={newCost.description}
        onChange={(e) =>
          setNewCost({ ...newCost, description: e.target.value })
        }
      />
      <input
        type="number"
        placeholder="Amount"
        value={newCost.amount}
        onChange={(e) => setNewCost({ ...newCost, amount: e.target.value })}
      />
      <input
        type="number"
        placeholder="Urgency (1-5)"
        value={newCost.urgency}
        onChange={(e) => setNewCost({ ...newCost, urgency: e.target.value })}
      />
      <button onClick={handleAddCost}>Add Unpaid Cost</button>

      {costs.map((cost) => (
        <div key={cost.id}>
          <p>
            {cost.description}: ${cost.amount} - Urgency: {cost.urgency}
          </p>
        </div>
      ))}
    </div>
  );
};

export default UnpaidCosts;
